database-object.create:

CREATE OR REPLACE FUNCTION function_vendor() RETURNS TRIGGER as	
$trigger_vendor$
BEGIN
IF TG_OP='INSERT' THEN
	UPDATE ACCOUNT SET OPENING_BALANCE = OPENING_BALANCE + NEW.BALANCE,CURRENT_BALANCE = CURRENT_BALANCE + NEW.BALANCE,TOTAL_BALANCE = TOTAL_BALANCE + NEW.BALANCE WHERE ID_ACCOUNT = NEW.ACCOUNTS_PAYABLE_ID;
RETURN NEW;
END IF;	
	RETURN NULL;
END;

 $trigger_vendor$ LANGUAGE plpgsql;
